var $getOwnPropertyNames = Object.getOwnPropertyNames;

Object.assign1 = function assign(target, source) {
  var props = $getOwnPropertyNames(source);
  var p,
      length = props.length;
  for (p = 0; p < length; p++) {
    target[props[p]] = source[props[p]];
  }
  return target;
};


