//init Sensors
exports.init = function(configSensors,board,five,cb){
  board.sensors = [];
  for (var i = 0; i < configSensors.length; i++) {
    board.sensors[configSensors[i].uid] = initFunctions[configSensors[i].type](five,configSensors[i].pin,configSensors[i].freq);
  }
  return cb(board);
}


var initFunctions = {};
//todo: comment
initFunctions.LD35 = function(five,pin,freq){
  var LD35 = new five.Sensor(pin);
  LD35.output = function(v){
    return {'celsius': (v * 500)/1024, 'voltage': v};
  }
  return LD35;
}

//todo: comment
initFunctions.Potentiometer = function(five,pin,freq){
  Potentiometer = new five.Sensor({
    pin: pin,
    freq: freq
  });
  Potentiometer.output = function(v){
    return {'ohm': v};
  }
  return Potentiometer;
}
