var five = require('johnny-five');
var initFunctions = {};

//todo: comment
initFunctions.LD35 = function(sensor){
  var LD35 = new five.Sensor(sensor.hardware);
  LD35.uid = sensor.uid;
  LD35.output = function(v){
    return {'celsius': (v * 500)/1024, 'voltage': v};
  }
  return LD35;
}

//todo: comment
initFunctions.Potentiometer = function(sensor){
  Potentiometer = new five.Sensor({
    pin: sensor.pin,
    freq: sensor.freq
  });
  Potentiometer.uid = sensor.uid;
  Potentiometer.output = function(v){
    return {'ohm': v};
  }
  return Potentiometer;
}

//init Sensors
exports.init = function(board,cb){
  board.sensors = [];
  return cb(board);
}

exports.registerSensor = function(hardware,block,boardId,cb){
  if(initFunctions[block.name]!==undefined){
    hardware.blocks[boardId].sensors[block.uid] = initFunctions[block.name](block);
    console.log("initiated Sensor: "+block.uid);
    return cb(hardware.blocks[boardId].sensors[block.uid]);
  }else{
    console.log("Sensor: Type is undefined!");
  }
}


