function add(block){
  var result = [];
  for(i in block.input){
    console.log("Add:");
    console.log(blocks[block.input[i].uid].output(this.value, block.input[i].type));
    result += blocks[block.input[i].uid].output(this.value, block.input[i].type);
  }

  for(i in pins){
    try{
      blocks[uid].value[pins[i].pid] = this.output(this.value,pins[i].type);
      app.io.broadcast('pid'+pins[i].pid,this.output(this.value,pins[i].type));
    }catch(e){
      console.log("Error at Add in ventanilla/logicTypes.js while broadcasting: " + e);
    }
  }
  setTimeout(function(){
    return Add(block);
  },250);
};
